mapper.update:
update likes
set type=#{type}
where uid = #{uid}
  and cid = #{cid}

mapper.delete:
delete
from likes
where uid = #{uid}
  and cid = #{cid}

mapper.select:
select sum(type) as 'like', truncate(count(0)-sum(type),0) as 'dislike', cid from likes where cid in
#{id}
group by cid

select id, uid, cid, type, ctime, mtime
from likes
where uid = #{uid}
  and cid = #{cid}

select id, uid, cid, type, ctime, mtime
from likes where uid=#{uid} and cid in

    #{id}
